var categories = ["All","Important Documents","Study Material","Laundry","Computer Equipment","Decorative","Leisure","Bedding","Kitchen","Basic Foods","Clothing","Other"];

var ttttcollege = angular.module('ttttcollege', []);

ttttcollege.controller('ItemsController', function($scope) {
  $scope.items = data;
  $scope.categories = categories;
  $scope.currentCat = categories[0];

  $scope.remaining = function() {
    var count = 0;
    angular.forEach($scope.items, function(item) {
      count += item.done ? 0 : 1;
    });
    return count;
  };
  $scope.filterByCategory = function(category) {
    var list = data;
    $scope.items = [];
    angular.forEach(list, function(item) {
      if(item.category === category) $scope.items.push(item);
    });
    $scope.currentCat = category;
  };

});